import React from 'react';
import PropTypes from 'prop-types';
import styles from './calendarBody.scss';

const CalendarBody = (props) => {
let newDate = new Date();
//   super(props);
//    this.state = {
//        date: newDate.getDate(),
//        currentMonth: newDate.getMonth(), 
//        currentYear: newDate.getFullYear()
//     }
    //let date = newDate.getDate();
   let currentMonth = newDate.getMonth();
     let currentYear = newDate.getFullYear();

let getDaysInMonth = function( month, year ) {
    return new Date( year, month, 0 ).getDate();
};

let daysInMonth = getDaysInMonth(
    /*this.state.*/currentMonth + 1,
   /*this.state.*/currentYear
);

let firstDayOfWeek = new Date(
     /*this.state.*/currentYear,
     /*this.state.*/ currentMonth ).getDay();


let lastDay = new Date(  /*this.state.*/currentYear,  /*this.state.*/currentMonth, 0 ).getDate();
    let day = 1;
    let dayOfNextMonth = 0;
    let cellText;
    const tds = ( i )  => {
        let td = [];
        const baseDayClass = 'basis class';
        for ( let y = 0; y < 7; y++ ) {
            let dayClass = baseDayClass;
            if ( i === 0 && y < firstDayOfWeek - 1  ) {
                cellText = lastDay - firstDayOfWeek + 2 + y;
            } else if ( day >= daysInMonth ) {
                dayOfNextMonth++;
                cellText = day;
                day = dayOfNextMonth;
            } else {
                cellText = day;
                if (  day === newDate.getDate() &&
                 /*this.state.*/currentYear === newDate.getFullYear() &&
                 /*this.state.*/currentMonth === newDate.getMonth() ) {
                    //day;
                    dayClass += ' ' + styles.actualDate;
                }
                //cellText = day;
                day++;
            }
            td.push( <td key={ y } className={dayClass}>{cellText}</td> );
        }
        return td;
    };

    const trs = ()  => {
        let tr = [];
        for ( let i = 0; i < 6/** && day <= daysInMonth*/; i++ ) {
            tr.push( <tr key={i}>{tds( i )}</tr> );
        }
        return   tr;
    };

    return (

        <div className="row">
            <div>
                <table>
                    <tbody>
                        { trs() }
                    </tbody>
                </table>
            </div>
        </div>
    );
};


CalendarBody.propTypes = {
    /** Description of prop "text". */

    text: PropTypes.string
};

CalendarBody.defaultProps = {
    text: 'defaultText'
};

export default CalendarBody;
