import React from 'react';
import PropTypes from 'prop-types';
import styles from './calendarBody.scss';


let newDate = new Date();
let date = newDate.getDate();
let currentMonth = newDate.getMonth() + 1;
let currentYear = newDate.getFullYear();
let getDaysInMonth = function( currentMonth, currentYear ) {
    return new Date( currentYear, currentMonth, 0 ).getDate();
};

let daysInMonth = getDaysInMonth(
    currentMonth + 1,
    currentYear
);


let firstDayOfWeek = new Date(
    currentYear,
    currentMonth ).getDay();
/**
 * General component description in JSDoc format. Markdown is *supported*.
 */
const CalendarBody = ( { text } ) => {

    const tds = ()  => {
        let td = [];
        for ( let y = 0; y < 7; y++ ) {
            td.push( <td onClick={console.log( 'hi' )} key={y}>12</td> );

        }

        return   td;
    };

    const trs = ()  => {
        let tr = [];
        for ( let i = 0; i < 5; i++ ) {
            tr.push( <tr key={i}>{tds()}</tr> );
        }

        return   tr;
    };


    return (

        <div className="row">
            <div>
                <table>
                    <tbody>

                        { trs() }

                    </tbody>
                </table>
            </div>
        </div>
    );
};


CalendarBody.propTypes = {
    /** Description of prop "text". */

    text: PropTypes.string
};

CalendarBody.defaultProps = {
    text: 'defaultText'
};

export default CalendarBody;
