import React from 'react';
import PropTypes from 'prop-types';
import styles from './calendarBody.scss';

let newDate = new Date();

let date = newDate.getDate();

let currentMonth = newDate.getMonth();

let currentYear = newDate.getFullYear();

let getDaysInMonth = function( month, year ) {
    return new Date( year, month, 0 ).getDate();
};

let daysInMonth = getDaysInMonth(
    currentMonth + 1,
    currentYear
);

let firstDayOfWeek = new Date(
    currentYear,
    currentMonth ).getDay();


let lastDay = new Date( currentYear, currentMonth, 0 ).getDate();

console.log( 'currentYear', currentYear );
console.log( 'currentMonth', currentMonth );
console.log( 'daysInMonth', daysInMonth );
console.log( 'firstDayOfWeek', firstDayOfWeek );
console.log( 'lastDay', lastDay );
console.log( 'newDate', newDate );
console.log( 'date', date );

let cellText = [];
const CalendarBody = ( ) => {
    let currentDay = 1;
    let dayOfNextMonth = 1;
    const tds = ( )  => {
        let td = [];
        for ( let y = 0; y < 7; y++ ) {
            td.push( <td key={ y }>{currentDay}</td> );
            currentDay++;
            if ( daysInMonth < currentDay ) {
                currentDay = dayOfNextMonth;
                dayOfNextMonth++;
            } else if ( currentDay === newDate.getDate() &&
            currentYear === newDate.getFullYear() &&
              currentMonth === newDate.getMonth() ) {
                currentDay;
            }
        }
        return td;
    };
    const trs = ()  => {
        let tr = [];
        for ( let i = 0; i < 5 && currentDay <= daysInMonth; i++ ) {
            tr.push( <tr key={i}>{tds( )}</tr> );
            console.log( 1, lastDay - firstDayOfWeek + 1 + i );
        }
        return   tr;
    };
    return (

        <div className="row">
            <div>
                <table>
                    <tbody>
                        { trs() }
                    </tbody>
                </table>
            </div>
        </div>
    );
};


CalendarBody.propTypes = {
    /** Description of prop "text". */

    text: PropTypes.string
};

CalendarBody.defaultProps = {
    text: 'defaultText'
};

export default CalendarBody;
