import React from 'react';
import PropTypes from 'prop-types';
// import Dummy from '../ui/dummys/Dummy';
import Button from 'ui/buttons/Button';
//import Button1 from 'ui/buttons/Button.1';
import Select from 'ui/form/Select';
import SVGPrevious from 'ui/svg/Previous';
import SVGNext from 'ui/svg/Next';
import styles from './calendar.scss';
import DaysOfWeek from 'mod/daysOfWeek/DaysOfWeek';
import CalendarBody from 'mod/calendarBody/CalendarBody';
//import CalendarBodyTest from 'mod/calendarBody/CalendarBodyTest';
import CalendarHeader from 'mod/calendarHeader/CalendarHeader';
import EventBody from 'mod/eventBody/EventBody';
//import GetCurrentDate from 'mod/calendarBody/CalendarBody';


/**
 * General component description in JSDoc format. Markdown is *supported*.
 */
const setPreviousMonthButton = () => {
    let newDate = new Date();
    let setNewMonth = newDate.getMonth();
    let newYear = newDate.getFullYear();

    if (setNewMonth === 0) {
      setNewMonth = 11;
      newYear--;
    } else {
      setNewMonth = newDate.getMonth() - 1;
    }
  };
const monthChoice = [
    { value: 'january', text: 'January' },
    { value: 'february', text: 'February' },
    { value: 'march', text: 'March' },
    { value: 'april', text: 'April' },
    { value: 'may', text: 'May' },
    { value: 'june', text: 'June' },
    { value: 'july', text: 'July' },
    { value: 'august', text: 'August' },
    { value: 'september', text: 'September' },
    { value: 'october', text: 'October' },
    { value: 'november', text: 'November' },
    { value: 'december', text: 'December' }
];

const yearChoice = [
    { value: 'december', text: '2010' },
    { value: 'january', text: '2011' },
    { value: 'february', text: '2012' },
    { value: 'march', text: '2013' },
    { value: 'april', text: '2014' },
    { value: 'may', text: '2015' },
    { value: 'june', text: '2016' },
    { value: 'july', text: '2017' },
    { value: 'august', text: '2018' },
    { value: 'september', text: '2019' },
    { value: 'october', text: '2020' },
    { value: 'november', text: '2021' },
    { value: 'december', text: '2022' },
    { value: 'april', text: '2023' },
    { value: 'may', text: '2024' },
    { value: 'june', text: '2025' }
];
     <div>
         <div className={styles.calendarHeader}><CalendarHeader /></div> 

      <div>
            <Button text="Month" />
            <Button text="Week" />
            <Button text="Day" />
        </div>

       <Select options={monthChoice} />
        <Select options={yearChoice} />
        <SVGPrevious width={20} height={10} />
        <Button text="Previous"  onclick={setPreviousMonthButton}/>
        <Button text="Next" />
        <SVGNext width={20} height={10} /> 
         {/* <Button text="Previous" />
        {/* <Button1 text="Previous" />
         <Button1 text="Next" />  */}
        {/* <Button text="Next" />  */}
        <DaysOfWeek />
        <CalendarBody />


        {/* <EventBody /> */}
    </div>


Calendar.propTypes = {
    /** Description of prop "text". */
    text: PropTypes.string,
    optionsState: PropTypes.string

};

Calendar.defaultProps = {
    text: 'defaultText',
    optionsState: 'text'

};

export default Calendar;
