import React from 'react';
import PropTypes from 'prop-types';
import styles from './selectMonth.scss';


/**
 * General component description in JSDoc format. Markdown is *supported*.
 */
const SelectMonth = ( { options } ) => (
    <label>
        <select className={styles.select}>
            options.map((entry) =>
            <option key={entry.text}>{entry.text}</option>
            );
        </select>
    </label>
);

SelectMonth.propTypes = {
    /** Description of prop "text". */

    options: PropTypes.string
};

SelectMonth.defaultProps = {
    options: [ { value: '0', text: 'bitte wählen' } ]
};

export default SelectMonth;
