import React from "react";
import PropTypes from "prop-types";
import styles from "./calendarHeader.scss";

// let changeHeader = () => {
//   let header = state.monthNames[state.getMonth()]+ " " + state.currentYear
// }

    //  setCurrentMonthTitle.innerHTML =
    //  calendarModel.months[calendarModel.currentMonth] +
    //  ' ' +
    //  calendarModel.currentYear;

const CalendarHeader = (props) => {
let newDate = new Date();
let currentYear = newDate.getFullYear();
let currentMonth = newDate.getMonth();
return (
  <div className={styles.calendarHeader}> 
  {props.monthNames[currentMonth] + " " + currentYear}
        </div>
)
};
CalendarHeader.propTypes = {

  optionsState: PropTypes.string
};

CalendarHeader.defaultProps = {
  text: "defaultText"
};

export default CalendarHeader;
