import React from 'react';
import PropTypes from 'prop-types';
import styles from './calendarBody.scss';


let newDate = new Date();
console.log( 'newDate', newDate );
let date = newDate.getDate();
console.log( 'date', date );
let currentMonth = newDate.getMonth() + 1;
console.log( 'currentMonth', currentMonth );
let currentYear = newDate.getFullYear();
console.log( 'currentYear', currentYear );
let getDaysInMonth = function( month, year ) {
    return new Date( currentYear, currentMonth, 0 ).getDate();
};
console.log( 'getDaysInMonth', getDaysInMonth() );

let daysInMonth = getDaysInMonth(
    currentMonth + 1,
    currentYear
);
console.log( 'daysInMonth', daysInMonth );

let firstDayOfWeek = new Date(
    currentYear,
    currentMonth ).getDay();
console.log( 'firstDay', firstDayOfWeek );
let lastDay = new Date(
    currentYear,
    currentMonth,
    0
).getDate();
console.log( 'lastDay', lastDay );

function getCurrentDate(  ) {
    return (
        currentYear + ' ' + currentMonth + ' ' + date
    );
}
console.log( 'getCurrentDate', getCurrentDate() );


/**
 * General component description in JSDoc format. Markdown is *supported*.
 */
const GetCurrentDate = ()  => {
    return (
        currentYear + ' ' + currentMonth + ' ' + date

    );
};
let cellText = '';
const CalendarBody = ( { text } ) => {

    const tds = ( )  => {
        let td = [];

        for ( let y = 0; y < 7; y++ ) {
            td.push( <td key={y}>{cellText}</td> );
            cellText++;


        }
        return   td;
    };

    const trs = ()  => {
        let tr = [];
        for ( let i = 0; i < 5 && date <= daysInMonth; i++ ) {
            tr.push( <tr key={i}>{tds( )}</tr> );
            cellText++;
            if ( i === 0 ) {
                cellText = lastDay - firstDayOfWeek + 2 ;
            }
        }

        return   tr;
    };


    return (

        <div className="row">
            <div> <GetCurrentDate />
                <table>
                    <tbody
                    >

                        { trs() }

                    </tbody>
                </table>
            </div>
        </div>
    );
};


CalendarBody.propTypes = {
    /** Description of prop "text". */

    text: PropTypes.string
};

CalendarBody.defaultProps = {
    text: 'defaultText'
};

export default CalendarBody;
